import Modal from "react-modal";
export default function MovieModal({ movie, isModalOpen, closeModal }) {
  return (
    <Modal
      isOpen={isModalOpen}
      onRequestClose={closeModal}
      contentLabel={movie.Title}
    >
        <div>
            <span onClick={closeModal}>&times;</span>
            <div>
                <img src={movie.Poster} />
            </div>
        </div>
    </Modal>
  );
}
