import Modal from "react-modal";

export default function MovieModal({ movie, isModalOpen, closeModal }) {
  return (
    <Modal
      contentLabel={movie.Title}
      isOpen={isModalOpen}
      onRequestClose={closeModal}
      className="modal"
      overlayClassName="modal-overlay"
    >
      <div className="modal-content">
        <span onClick={closeModal}>
          <h3>&times;</h3>
        </span>
        <div>
          <img src={movie.Poster} alt={movie.Title}></img>
          <div className="modal-header">
              <h2>{movie.Title}</h2>
              <h4>{movie.Year}</h4>
            <p>
              <br />
              <strong>Genre : </strong> {movie.Genre}
              <br />
              <strong>Duration of the Movie : </strong> {movie.Runtime}
              <br /><strong>Duration of the Movie : </strong> {movie.Runtime}
              <br />
              <strong>imDb Rating : </strong> {movie.imdbRating}
              <br />
              <strong>Movie Plot : </strong> {movie.Plot}
              <br />
              <strong>Actors : </strong> {movie.Actors}
              <br />
              <strong>Writer : </strong> {movie.Writer}
            </p>
          </div>
        </div>
      </div>
    </Modal>
  );
}
